import React from 'react'
import {Grid, List, ListItem, ListItemIcon, ListItemText, makeStyles, Typography} from '@material-ui/core'
import { Link } from 'react-router-dom'
import { useSideBarMenuConfig } from '../../_config/sideBarMenuConfig'

const styles = makeStyles({
    root:{
        marginTop: '3vh',
        padding: '1vw'
    },
    menuItem:{
        paddingLeft: '1vw',
        color: 'white'
    },
    subMenuItem:{
        paddingLeft: '3vw',
        color: 'white'
    }
})

interface IProps{
    open: boolean
    handleClose: () => void
}

const SideMenu: React.FC<IProps> = (props) =>{

    const {open, handleClose} = props
    const useStyle = styles()

    const sideBarConfig = useSideBarMenuConfig()

    const sideBarMenu = React.useMemo(() =>{

        return(
            <List>
                {sideBarConfig.map((config) =>{
                    return(
                        <Grid
                            container>
                            <ListItem key={config.key} selected = {config.expanded} onClick = {config.handleSelected.bind(null, config.key)}>
                                <ListItemText>{config.title}</ListItemText>
                            </ListItem>
                            {config.expanded && (
                                config.items.map((item) =>{
                                    return(
                                        <ListItem key={item.key} selected = {item.selected} onClick = {item.handleSelected.bind(null, item.key)}>
                                            <Link to = {item.path}>
                                                <ListItemText>{item.value}</ListItemText>
                                            </Link>
                                        </ListItem>
                                    )
                                })
                            )}
                        </Grid>
                    )
                })}

            </List>
        )
    },[sideBarConfig]) 

    return(
        <Grid
            className = {useStyle.root}
            container>
                {sideBarMenu}
        </Grid>
    )
}

export default SideMenu;
