import { Grid, TextField, CircularProgress } from '@material-ui/core'
import React from 'react'
import {IImageUploadResult} from '../../_hooks/useImageConverter'

interface IProps{
    isReadOnly: boolean
    width: number
    height: number
    isLoading: boolean
    imageSrc: string
}

const ImageInput: React.FC<IProps> = (props) =>{

    const {isReadOnly, width, height, isLoading, imageSrc, handleImageChanged} = props


    return(
        <Grid
            style={{display:'flex', flexDirection:'column', justifyItems:'center', alignItems:'center' }}
            item>
            <Grid
                 xs={12}
                 sm={4}
                item>
                <img width={width} height={height} src={imageSrc}/>
            </Grid>
            <Grid
                xs={12}
                sm={4}
                item>
                {!isReadOnly && (
                    <TextField hidden={true} type='file' onChange={imageService.handleChange}/>
                )}
            </Grid>
            {isLoading && (
                <CircularProgress/>
            )}    
        </Grid>
    )
}

export default ImageInput
