import React from 'react'
import useApi from '../../_hooks/useApi'
import { IMenu } from '../../_interfaces/IMenu'
import apiConfig from '../../_config/apiConfig.json'
import { SetMenuCollection } from '../../_redux/_appStateStore/appStoreAccessor'
import { useSelector } from 'react-redux'
import { IAppState } from '../../_interfaces/IAppState'

interface IProps{
    isEditMode?: boolean
}

const CookingBookDataService: React.FC<IProps> = (props) =>{

    const menuDataService = useApi<IMenu>({serviceUri: apiConfig.baseUrl + apiConfig.cookingBook.getMenuCollection, params: null})

    const menuCollection: IMenu[] = useSelector<IAppState, IMenu[]>(state => state.menuCollection)

    if(!menuDataService.dataBound){

        return null
    }

    SetMenuCollection(menuDataService.items)
   

    return(
        <div></div>
    )
}

export default CookingBookDataService
