import React from 'react'
import {Grid, List, ListItem, ListItemIcon, ListItemText, makeStyles} from '@material-ui/core'
import { Link } from 'react-router-dom'
import { useSideBarMenuConfig } from '../../_config/sideBarMenuConfig'

const styles = makeStyles({
    root:{
        marginTop: '3vh'
        // display:'flex',
        // flexDirection:'column',
        // padding: '1vw'
    }
})

interface IProps{
    open: boolean
    handleClose: () => void
}

const SideMenu: React.FC<IProps> = (props) =>{

    const {open, handleClose} = props
    const useStyle = styles()

    const sideBarConfig = React.useMemo(() =>{
        return useSideBarMenuConfig()
    },[])

    const sideBarMenu = React.useMemo(() =>{

        return(
            <List>
                {sideBarConfig.map((config, index) =>{
                    return(
                        <>
                            <ListItem key={config.key} selected = {config.expanded} onClick = {config.handleSelected.bind(null, config.key)}>{config.title}</ListItem>
                        </>
                    )
                })}

            </List>
        )
    },[sideBarConfig]) 

    return(
        <Grid
            className = {useStyle.root}
            container>

        </Grid>
    )
}

export default SideMenu;
