import { Grid, TextField } from '@material-ui/core'
import React from 'react'
import { useImageUpload } from '../../_hooks/useImageUpload'

const LandingPage: React.FC = () => {
    
    const imageService = useImageUpload("")
    
    const [buffer, setBuffer] = React.useState<Blob>()

    const imgTag = React.useMemo(() =>{
    
        const src = imageService.image !== undefined ? URL.createObjectURL(imageService.image) : ""
        return(
            <img src={src} width={200} height={200}>

            </img>
        )
    },[buffer])


    console.log(imageService.image)

    const handleChange = React.useCallback(async (e: React.ChangeEvent<HTMLInputElement>) =>{
        e.preventDefault()

        if(e.target.files !== null && e.target.files[0]){

            const file = e.target.files[0]

            const buffer = await file.arrayBuffer()
            const blob = new Blob([buffer])

            console.log(blob)

            setBuffer(blob)
        }
    },[])

    return(
        <Grid container>
           {imgTag}
            <TextField hidden={true} type='file' onChange={imageService.handleChange}/>
        </Grid>
    )
}

export default LandingPage
