import { Checkbox, Grid, makeStyles, Tooltip, Typography } from '@material-ui/core'
import React from 'react'

const styles = makeStyles({
    cell:{
        '&:hover':{
            cursor: 'pointer'
        }
    }
})

interface IProps{
    id: number
    hasCheckbox?: boolean
    value: string
    selectedItems: number[]
    handleSelectedItems: (id: number[]) => void

}

const TableCell: React.FC<IProps> = (props) => {

    const {id, hasCheckbox, selectedItems, value, handleSelectedItems} = props

    const [selected, setSelected] = React.useState<boolean>(false)

    const useStyle = styles()


    const handleSelect = React.useCallback((id: number) =>{
        if(selectedItems.findIndex(x => x) === -1){
            setSelected(true)
            selectedItems.push(id)

            handleSelectedItems(selectedItems)
        }else{
            selectedItems.splice(selectedItems.findIndex(x => x === id))
            setSelected(false)
            handleSelectedItems(selectedItems)
        }
    },[selectedItems, handleSelectedItems])

    const cellComponent = React.useMemo(() =>{
        return(
            <Grid
                className={useStyle.cell}
                container>
                {hasCheckbox && (
                    <Tooltip title='delete'>
                        <Checkbox checked={selected} onChange={handleSelect?.bind(null, id)}/>
                    </Tooltip>
                )}
                {!hasCheckbox &&(
                <Tooltip title={value}>
                    <Typography>{value}</Typography>
                </Tooltip>
                )}
            </Grid>
        )
    },[handleSelect, selected,  hasCheckbox, id, useStyle, value])

    return(
        cellComponent
    )
}

export default TableCell
