import React from 'react'
import useApi from '../../../_hooks/useApi'
import { ILogmessage } from './interfaces/ILogmessage'
import LoggingPageContainer from './loggingPageContainer'
import apiConfig from '../../../_config/apiConfig.json'
import { getLogMessageRow } from '../mapper'

export interface logMessageTableState{
    allSelected: boolean
    ids: number[] 
}
interface IProps{

}

const LoggingDataservice: React.FC<IProps> = (props) =>{
   
    const logDataService = useApi<ILogmessage>({
        serviceUri: apiConfig.baseUrl + apiConfig.administration.logging,
        params: null
    })

    const [selectedItems, setSelectedItems] = React.useState<logMessageTableState>({allSelected: false, ids:[]})

    const logMessageRows = React.useMemo(() =>{
        return logDataService.items.map((item) =>{
            return getLogMessageRow(item)
        })
    },[logDataService])

    const handleSelectedItemsChanged = React.useCallback((items: number[]) =>{
    //     const state = {...selectedItems.selectAll, ...items}
    //    setSelectedItems(state)
    },[selectedItems])

    const handleSelectAll = React.useCallback(() => {
        // if(selectedItems.length === logMessageRows.length){
        //     const state = [true, []]
        //     setSelectedItems(state)
        // }else{
        //     const ids = logMessageRows.map((msg) =>{
        //         return msg.id
        //     })
        //     const state = [false, ids]
        //     setSelectedItems(state)
        // }
    },[logMessageRows, selectedItems.length])
   
    if(logDataService.items[0] === undefined){
        return null
    }
    
    return(
        <LoggingPageContainer
            logmessages={logMessageRows}
            selectedItems={selectedItems}
            handleSelectedItems={handleSelectedItemsChanged}
            handleSelectAll={handleSelectAll}
            />
    )
}

export default LoggingDataservice
