import React from 'react'
import MenuInputComponent from './MenueInputComponent'
import {IIngredient, IMenu} from '../../_interfaces/IMenu'
import { MenuTypeEnum } from '../../_enums/menuEnums'
import { Grid, MenuItem, List, Button } from '@material-ui/core'
import IngrdientListtem from './IngredientListItem'
import IngrdientList from './IngredientList'
import SubHeaderBar from '../../_components/_appBars/subHeader'

interface IProps{
    selectedMenu: IMenu
    isReadOnly: boolean
    handleSave: (menu: IMenu) => Promise<void>
}

const MenuContainer: React.FC<IProps> = (props) =>{

    const {selectedMenu, isReadOnly, handleSave} = props

    const [menu, setMenu] = React.useState<IMenu>(selectedMenu)
    
    React.useEffect(() =>{
        setMenu(selectedMenu)
    },[setMenu, selectedMenu])

    const original = React.useMemo(() =>{
        return selectedMenu
    },[selectedMenu])

    const disabled = React.useMemo(() =>{
        return (menu.name === selectedMenu.name
            && menu.description === selectedMenu.description
            && menu.howTo === selectedMenu.howTo
            && menu.menuType === selectedMenu.menuType)
    },[menu, selectedMenu])

    const onTitleChanged = React.useCallback((name: string) =>{
        const data: IMenu = {...menu, name}
        setMenu(data)
    },[menu])

    const onDescriptionChanged = React.useCallback((description: string) =>{
        const data: IMenu = {...menu, description}
        setMenu(data)
    },[menu])

    const onHowToChanged = React.useCallback((howTo: string) =>{
        const data: IMenu = {...menu, howTo}
        setMenu(data)
    },[menu])

    const onMenuTypeChanged = React.useCallback((value: number) =>{
        const data: IMenu = {...menu, menuType: value}
        setMenu(data)
    },[menu])

    const menuTypes = React.useMemo(() => {
        const elements = [] as JSX.Element[]

        elements.push(<MenuItem key = {-1} id='-1' value={-1} selected={menu.menuType === MenuTypeEnum.None}>Select Menu Type</MenuItem>)
        elements.push(<MenuItem key = {0} id={'0'} value = {0} selected={menu.menuType === MenuTypeEnum.Pasta} >{MenuTypeEnum[MenuTypeEnum.Pasta]}</MenuItem>)
        elements.push(<MenuItem key = {1} id={'4'} value={4} selected={menu.menuType === MenuTypeEnum.Meat} >{MenuTypeEnum[MenuTypeEnum.Meat]}</MenuItem>)
        elements.push(<MenuItem key = {2} id={'3'} value={3} selected={menu.menuType === MenuTypeEnum.Other} >{MenuTypeEnum[MenuTypeEnum.Other]}</MenuItem>)

        return elements
    },[menu.menuType])

    const handleAddIngredient = React.useCallback(() => {
        const update: IMenu = {...menu}
        update.ingredients.push({} as IIngredient)
        setMenu(update)

    },[menu, setMenu])

    const onCancel = React.useCallback(() =>{
        setMenu(original)
    },[original, setMenu])

    const onSave = React.useCallback(async () => {
        await handleSave(menu)
    },[menu, handleSave])

    const ingredientList = React.useMemo(() =>{
        return(
            <List style={{width: '90%', padding: '2vh', marginTop:'1.5vh'}}>
                {menu?.ingredients?.length &&  menu?.ingredients?.map((ingredient, index) => {
                    return(
                        <IngrdientListtem key={index} ingredient={ingredient} isReadOnly = {isReadOnly}/>
                    )
                })}
            </List>
        )
    },[isReadOnly, menu])

    return(
        <Grid
            container
            item
            justifyContent='center'
            xs={11}
            xl={11}>
            <Grid
                container>
                    <SubHeaderBar title='Info' variant='h5'/>
                    <MenuInputComponent
                                label='Name'
                                value={menu?.name?? ""}
                                fullWidth={true} 
                                isReadonly={isReadOnly}
                                onValueChanged={onTitleChanged} />
                    <MenuInputComponent
                                label='Description'
                                isMultiRow={true}
                                maxRows={5}
                                fullWidth={true} 
                                value={menu?.description?? ""} 
                                isReadonly={isReadOnly}
                                onValueChanged={onDescriptionChanged}/>
                    <MenuInputComponent
                                label='How To'
                                isMultiRow={true}
                                maxRows={20}
                                fullWidth={true} 
                                value={menu?.howTo?? ""} 
                                isReadonly={isReadOnly}
                                onValueChanged={onHowToChanged}/>
                    <MenuInputComponent
                                label='Menu Type'
                                isMultiRow={true}
                                maxRows={5}
                                fullWidth={true} 
                                value={menu.menuType ?? -1} 
                                isReadonly={isReadOnly}
                                hasSelect={true}
                                selectElements={menuTypes}
                                onSelectChanged={onMenuTypeChanged}/>
            </Grid>
            <Grid
                item
                xs={12}>
                    <IngrdientList ingedientList={ingredientList}/>
            </Grid>
            {!isReadOnly && (
            <Grid
                justifyContent='flex-end'
                style={{margin: '2vw'}}
                container>
                    <Button disabled={disabled} onClick={onCancel}>Cancel</Button>
                    <Button disabled={disabled} onClick={onSave}>Save</Button>
            </Grid>
            )}
        </Grid>
    )
}

export default MenuContainer
