import { Grid, makeStyles } from '@material-ui/core'
import React from 'react'
import { IMenu } from '../../_interfaces/IMenu'
import { IMenuLayoutConfig } from './interfaces/IMenueLayoutConfig'
import MenuFilter from './MenuFilter'
import MenuList from './menuList'

interface IProps{
   layoutConfig: IMenuLayoutConfig
   menuCollection: IMenu[]
   handleSelectMenu: (id: number) => void
}

const styles = makeStyles((theme) => ({
    root:{
        display: 'flex',
        justifyContent: 'center',
        width: '98vw',
        padding:'1vw'      
    }
}))

const MenuLayout: React.FC<IProps> = (props) => {

    const {layoutConfig, menuCollection, handleSelectMenu} = props

    const useStyle = styles();


    return(
        <Grid
            className={useStyle.root}
            container>
            {layoutConfig.hasFilter &&(
            <Grid
                item
                xs={12}>
                    <MenuFilter menuConfig={layoutConfig}/>
            </Grid>
            )}
            <Grid
                container>
                {layoutConfig.hasMenuList && (
                <Grid item style={{padding:'1vw'}}>
                    <MenuList 
                        menuCollection={menuCollection} 
                        selectedMenuId={layoutConfig.selectedMenuId} 
                        handleSelectMenu={handleSelectMenu}/>
                </Grid>
                )}
                <Grid item>
                    Context
                </Grid>
            </Grid>
        </Grid>
    )
}

export default MenuLayout
