import { rejects } from "assert";
import { resolve } from "path/posix";
import React from "react";
import apiConfig from '../_config/apiConfig.json'

export interface IImageUploadResult{
    buffer: string | ArrayBuffer | null
    file: FormData|null
    getBase64: (file: File) => void
}

export const useImageUpload = (src: ArrayBuffer | string): IImageUploadResult =>{

    const [image, setImage] = React.useState<ArrayBuffer|string>(src);    

    const callBack = (buffer: ArrayBuffer) =>{
        setImage(buffer)
    }

    const fetchData = async (form: Uint32Array) =>{
        await fetch(apiConfig.baseUrl + apiConfig.cookingBook.image, {method: 'POST', mode: 'cors', body: JSON.stringify(form), headers: {"Content-Type":"application/problem+json"}})
    }

    const loadData = async (file: File) => {
        return new Promise((resolve, reject) =>{
            const reader = new FileReader()
            reader.readAsDataURL(file)

            reader.onload = () =>{
                resolve(reader.result)
            }

            reader.onerror = () => {
                reject("Could not load image!")
            }
        })
    }

    const getBase64 = React.useCallback(async (file: File) => {
        
        const data = await loadData(file)
        
        console.log(data)
        
    },[])

    return {
        buffer: image,
        file: file,
        getBase64
    }
}
