import React from 'react'
import PageBodyLayout from '../../../_layout/pageBodyLayout'
import MenuInputComponent from '../MenueTitleComponent'
import {IMenu} from '../../../_interfaces/IMenu'
interface IProps{

}

const AddMenuContainer: React.FC<IProps> = (props) =>{

    const [menu, setMenu] = React.useState<IMenu>({} as IMenu)

    const onTitleChanged = React.useCallback((title: string) =>{
        const data: IMenu = {...menu, name: title}
        setMenu(data)
    },[])

    return(
        <PageBodyLayout
            hasCancelBtn = {true}
            hasSaveBtn = {true}>
               <MenuInputComponent title={menu?.name?? ""} onTitleChange={onTitleChanged} isReadonly={false}/>
               <MenuInputComponent title={menu?.description?? ""} onTitleChange={onTitleChanged} isReadonly={false}/>
        </PageBodyLayout>
    )
}

export default AddMenuContainer
