import React from 'react'
import {Divider, Drawer, Grid, ListItem, ListItemIcon, ListItemText, makeStyles} from '@material-ui/core'
import { Link } from 'react-router-dom'
import { Close } from '@material-ui/icons'
import { ISidebarMenuConfig } from '../../_interfaces/ISidebarMenuConfig'
import useApi from '../../_hooks/useApi'

const styles = makeStyles({
    root:{
        marginTop: '3vh'
        // display:'flex',
        // flexDirection:'column',
        // padding: '1vw'
    }
})

interface IProps{
    open: boolean
    handleClose: () => void
}

const SideMenu: React.FC<IProps> = (props) =>{

    const {open, handleClose} = props
    const useStyle = styles()

    const dataService = useApi({serviceUri:"http://familybuttler:55321/api/I18n/GetValues/app_appMenuEn", params: null});
   
    if(!dataService.dataBound){
        return null
    }

    console.log(dataService.items);
    return(
        <Drawer
            anchor='left'
            open = {open}
            onClose={handleClose}>
            <Grid
                className={useStyle.root}
                container>
                <ListItem onClick={handleClose}>
                    <ListItemIcon>
                        <Close/>
                    </ListItemIcon>
                    {open &&(
                    <ListItemText>
                        <Link to='/'>Back</Link>
                    </ListItemText>
                    )}
                </ListItem>
                <ListItem onClick={handleClose}>
                    <ListItemIcon>
                        <Close/>
                    </ListItemIcon>
                    {open && (
                    <ListItemText>
                        <Link to='/1'>To</Link>
                    </ListItemText>
                    )}
                </ListItem>
            </Grid>
        </Drawer>
    )
}

export default SideMenu;
