import React from 'react'
import {Drawer, Grid, makeStyles} from '@material-ui/core'
import HeaderBar from '../_components/_appBars/headerBar'

const styles = makeStyles({
    root:{
        display:'flex'
    },
    headerContainer:{
        display:'flex',
        height: '5vh'
    },
    bodyContainer:{
        display:'flex',
        flexDirection: 'row',
        height:'95vh'
    },
    menuContainer:{
        backgroundColor: 'red'
    },
    contextContainer:{
        backgroundColor: 'green'
    }
})

interface IProps{
    title: string
}

const PageLayout: React.FC<IProps> = (props) => {

    const {title, children} = props

    const useStyle = styles();
    return(
        <Grid
            container
            className={useStyle.root}>
            <Grid
                container
                className={useStyle.headerContainer}>
                    <HeaderBar title={title}/>
            </Grid>
            <Grid
                container
                className={useStyle.bodyContainer}>
                <Grid
                    item
                    xs={2}
                    className={useStyle.menuContainer}>
                        Test
                </Grid>
                <Grid
                    item
                    xs={10}
                    className={useStyle.contextContainer}>
                     {children}
                </Grid>
               
            </Grid>
            
        </Grid>
    )
}

export default PageLayout;
