import { Grid, makeStyles, MenuItem, Container } from '@material-ui/core'
import React from 'react'
import InputTextField from '../../_components/_inputs/InputTextField'
import { MenuTypeEnum } from '../../_enums/menuEnums'

interface IProps{
    filterText: string
    selectedMenuType: MenuTypeEnum
    onFilterTextChange: (filterText: string) => void
    onMenuTypeChange: (menuType: number) => void
}

const styles = makeStyles((theme) => ({
    root:{
        backgroundColor: 'lightgray',
        padding:'1vh',
        height: 'auto',
        width:'100vw'
    }
}))

const MenuFilter: React.FC<IProps> = (props) => {

    const {filterText, selectedMenuType, onFilterTextChange, onMenuTypeChange} = props
    const useStyle = styles()

    const menuTypeItems = React.useMemo(() =>{
        const items: JSX.Element[] = []

        items.push(<MenuItem key={-1} id ='-1' value={-1} selected={selectedMenuType === MenuTypeEnum.None}>{MenuTypeEnum[MenuTypeEnum.None]}</MenuItem>)
        items.push(<MenuItem key={0} id = '0' value={0} selected={selectedMenuType === MenuTypeEnum.Pasta}>{MenuTypeEnum[MenuTypeEnum.Pasta]}</MenuItem>)
        items.push(<MenuItem key={4} id = '4' value={4} selected={selectedMenuType === MenuTypeEnum.Meat}>{MenuTypeEnum[MenuTypeEnum.Meat]}</MenuItem>)
        items.push(<MenuItem key={3} id = '3' value={3} selected={selectedMenuType === MenuTypeEnum.Other}>{MenuTypeEnum[MenuTypeEnum.Other]}</MenuItem>)

        return items
    },[selectedMenuType])

    return (
        <Grid 
            container
            className={useStyle.root}>
            <Grid 
                item
                xs={6}
                sm={6}
                md={6}
                lg={6}>
                {/* <InputTextField 
                    label='Filter' 
                    value={filterText} 
                    onValueChange={onFilterTextChange} /> */}
                </Grid >
            <Grid
                item
                xs={6}
                md={6}
                lg={6}>
                {/* <InputTextField 
                    label='MenuType' 
                    value={selectedMenuType ?? -1} 
                    hasSelect={true}
                    selectElements={menuTypeItems}
                    onSelect={onMenuTypeChange}/> */}
            </Grid >
        </Grid>
    )
}

export default MenuFilter
