import React from "react";

export interface IImageUploadResult{
    buffer: string | ArrayBuffer | null
    file: FormData|null
    getBase64: (file: File) => void
}

export const useImageUpload = (src: ArrayBuffer | string): IImageUploadResult =>{

    const [image, setImage] = React.useState<ArrayBuffer|string>(src);    
    const [file, setFile] = React.useState<FormData|null>(null)

    const callBack = (buffer: ArrayBuffer) =>{
        setImage(buffer)
    }

    const getBase64 = React.useCallback((file: File) => {
        let reader = new FileReader();
        
        const form = new FormData()
        form.append('img', file)

        setFile(form)

        reader.readAsDataURL(file);
        reader.onload = async  () => {
            callBack(reader.result as ArrayBuffer)
        };
        reader.onerror = function (error) {
            console.log('Error: ', error);
        };
    },[])

    return {
        buffer: image,
        file: file,
        getBase64
    }
}
