import { Grid, makeStyles } from '@material-ui/core'
import React from 'react'
import InputTextField from '../../_components/_inputs/InputTextField'


const styles = makeStyles((theme) => ({
    root:{
        display: 'flex',
        justifyContent: 'center',
        flexDirection:'row',
        [theme.breakpoints.down(900)]:{
            display: 'flex',
            justifyContent:'center',
            padding:'1vw',
            marginTop: '2vh'
        }
    },
    item:{
        padding: '.5vw',
    }
}))

interface IProps{
    label?: string
    value: string | number
    img?: any
    isReadonly: boolean
    fullWidth?: boolean
    isMultiRow?: boolean
    maxRows?: number
    hasSelect?: boolean
    selectElements?: JSX.Element[]
    onValueChanged?: (title: string) => void
    onSelectChanged?: (value: number) => void

}

const MenuInputComponent: React.FC<IProps> = (props) =>{

    const {label, value, isReadonly, img, fullWidth, isMultiRow, maxRows, hasSelect, selectElements, onSelectChanged, onValueChanged} = props

    const useStyle = styles()

    return(
            <Grid
                item
                xs={11}
                sm={11}
                md={11}
                lg={11}
                xl={11}>
                    <InputTextField 
                        label={label?? ""}
                        value={value}
                        isReadOnly={isReadonly}
                        multiLine={isMultiRow}
                        maxRows={maxRows}
                        fullWidth={fullWidth}
                        hasSelect={hasSelect}
                        selectElements={selectElements}
                        onValueChange={onValueChanged} 
                        onSelect={onSelectChanged}/>
            </Grid>
    )
}

export default MenuInputComponent
