import { Container, Grid, makeStyles } from '@material-ui/core'
import React from 'react'
import { IMenu } from '../../_interfaces/IMenu'
import { IMenuLayoutConfig } from './interfaces/IMenueLayoutConfig'
import MenuFilter from './MenuFilter'
import MenuList from './menuList'
import {MenuTypeEnum} from '../../_enums/menuEnums'
import MenuContainer from './MenuContainer'


interface IProps{
   layoutConfig: IMenuLayoutConfig
   selectedMenu: IMenu
   menuCollection: IMenu[]
   handleSelectMenu: (id: number) => void
}

const styles = makeStyles((theme) => ({
    filterContext:{
        display:'flex',
        justifyContent:'center'
    },
    contextContainer:{
        paddingTop: '1vh',
        display:'flex',
        width:'98vw',
        // maxHeight:'80vh',
        padding:'1vw',
        flexDirection:'column',
        overflow:'scroll'
    
    }
}))

const MenuLayout: React.FC<IProps> = (props) => {

    const {layoutConfig, menuCollection, selectedMenu, handleSelectMenu} = props
    const [filterText, setFilterText] = React.useState<string>("")
    const [menuType, setMenuType] = React.useState<number>(MenuTypeEnum.None)
    
    const useStyle = styles();

    const onFilterTextChange = React.useCallback((filterText: string) =>{
        setFilterText(filterText)
    }, [setFilterText])

    const onMenuTypeChange = React.useCallback((menuType: number) =>{
        setMenuType(menuType)
    }, [setMenuType])

    const filteredMenuItems = React.useMemo(() =>{
        
        if(filterText === '' && menuType === MenuTypeEnum.None){
            return menuCollection
        }

        const menusByFiltertext = menuCollection.filter(item => filterText.length > 0 ? item.name.toLocaleLowerCase().startsWith(filterText.toLocaleLowerCase()) : item)
        
        if(menuType === MenuTypeEnum.None){
            return menusByFiltertext
        }

        return menusByFiltertext.filter(item => item.menuType === menuType)

    },[menuCollection, filterText, menuType])

    return(
            <Grid
                direction='row'
                spacing={2}
                style={{ padding:'2vw', backgroundColor: 'red' }}
                container>
                <Grid
                    direction='row'
                    // spacing={1}
                    container>
                        <MenuFilter 
                            filterText={filterText}
                            selectedMenuType={menuType}
                            onFilterTextChange={onFilterTextChange}
                            onMenuTypeChange={onMenuTypeChange}/>
                </Grid>
                <Grid
                    direction='column'
                    // spacing={1}
                    container>
                    <Grid
                        item
                        xs={12}
                        sm={12}
                        md={12}
                        lg={12}
                        xl={3}>
                            T2
                    </Grid>
                    <Grid
                        item
                        xs={12}
                        sm={12}
                        md={12}
                        lg={12}
                        xl={8}
                        >
                            T3
                    </Grid>
                </Grid>
            </Grid>
    )
}

export default MenuLayout

