import { Grid, makeStyles, TextField } from '@material-ui/core'
import { Autocomplete } from '@material-ui/lab'
import React from 'react'
import InputTextField from '../../_components/_inputs/InputTextField'
import { IAutocompleteItem } from '../../_interfaces/IAutocompleteItem'
import { IMenu } from '../../_interfaces/IMenu'

const styles = makeStyles((theme) => ({
    menuItemContainer:{
        padding:'1vw'
    }
}))

interface IProps{
    menuCollection: IMenu[]
    title: string
    value: string
    handleChange: (value: string) => void
}

const MenuAutocompleteInput: React.FC<IProps> = (props) =>{

    const {menuCollection, title, value, handleChange} = props
    const [text, setText] = React.useState<string>(value)

    const useStyle = styles()
    
    const items = React.useMemo(() =>{
        return menuCollection.map((menu, index) =>{
            return(
                {id: index, label: menu.name}
            )
        })
    },[])

    const onChange = React.useCallback((e: React.ChangeEvent<{}>, item: IAutocompleteItem | null) =>{
        
        console.log("ITEM: ")
        console.log(item?.label)
        
        handleChange(item?.label?? "")
    },[handleChange])

    const handleInputChange = React.useCallback((e: React.ChangeEvent<HTMLInputElement>) =>{

        const index = items.findIndex(x => x.id === -1) ?? -1

        if(index === -1){
            items.push({id: -1, label: text})
        }
        setText(e.currentTarget.value)
    },[setText])

    return(
        <Grid
            className={useStyle.menuItemContainer}
            container
            item
            direction='row'
            spacing={2}
            justifyContent='center'>
            <Grid
                item
                xs={11}
                sm={11}
                md={11}
                lg={11}
                xl={11}>
                <Autocomplete
                    options={items}
                    noOptionsText={text}
                    getOptionLabel={(item) => item.label?? text}
                    onChange={(e: React.ChangeEvent<{}>, item: IAutocompleteItem | null) => onChange(e, item?? {id: -1, label: text})}
                    renderInput={(params) => <TextField 
                                                {...params} 
                                                fullWidth={params.fullWidth} 
                                                value={text} 
                                                label={title} 
                                                onChange={handleInputChange} />}
                    />
                </Grid>
            </Grid>
    )
}

export default MenuAutocompleteInput
