import { Grid, TextField } from '@material-ui/core'
import React, { MouseEvent } from 'react'

const LandingPage: React.FC = () => {
    
    const [selectedFile, setFile] = React.useState<File|null>(null);


    const handleChange = React.useCallback(async (e: React.ChangeEvent<HTMLInputElement>) =>{
        
        if(e.target.files !== null){
            setFile(e.target.files[0])
            
            console.log('here we finally ve the file as a ArrayBuffer : ', await selectedFile?.arrayBuffer());

            
        }
       
    },[])

   
    return(
        <Grid container>
            <TextField type='file' onChange={handleChange}/>
            <Grid container>
                {selectedFile !== null && (
                    <img src={selectedFile.webkitRelativePath} width={400} height={400}>

                    </img>
                )}
            </Grid>
        </Grid>
    )
}

export default LandingPage
