import { Grid, makeStyles, MenuItem } from '@material-ui/core'
import React from 'react'
import InputTextField from '../../_components/_inputs/InputTextField'
import { IMenuLayoutConfig } from './interfaces/IMenueLayoutConfig'

interface IProps{
    menuConfig: IMenuLayoutConfig
    filterText: string
    onFilterTextChange: (filterText: string) => void
}

const styles = makeStyles((theme) => ({
    root:{
        display: 'flex',
        justifyContent: 'space-around',
        backgroundColor: 'lightgray',
        height: '8vh'
    }
}))

const MenuFilter: React.FC<IProps> = (props) => {

    const {menuConfig, filterText, onFilterTextChange} = props
    const useStyle = styles()

    const menuTypeItems = React.useMemo(() =>{
        const items: JSX.Element[] = []

        items.push(<MenuItem value={-1}>None</MenuItem>)

        return items
    },[])

    return (
        <Grid 
            container 
            className={useStyle.root}>
           <Grid 
                item>
                <InputTextField 
                    label='Filter' 
                    value={filterText} 
                    onValueChange={onFilterTextChange}  />
            </Grid >
            <Grid 
                item>
                 <InputTextField 
                    label='MenuType' 
                    value={''} 
                    hasSelect={true}
                    selectElements={menuTypeItems} />
            </Grid >
        </Grid>
    )
}

export default MenuFilter
