import React from 'react'
import PageBodyLayout from '../../../_layout/pageBodyLayout'
import MenuInputComponent from '../MenueTitleComponent'

interface IProps{

}

const AddMenuContainer: React.FC<IProps> = (props) =>{

    const [menuTitle, setMenuTitle] = React.useState<string>("")

    const onTitleChanged = React.useCallback((title: string) =>{
        setMenuTitle(title)
    },[])

    return(
        <PageBodyLayout
            hasCancelBtn = {true}
            hasSaveBtn = {true}>
               <MenuTitle title={menuTitle} onTitleChange={onTitleChanged} isReadonly={false}/>
        </PageBodyLayout>
    )
}

export default AddMenuContainer
