import React from 'react'
import {Grid, makeStyles, Paper} from '@material-ui/core'
import HeaderBar from '../_components/_appBars/headerBar'
import SideMenu from '../_components/_menus/sideMenu'

const styles = makeStyles({
    root:{
        display:'flex'
    },
    headerContainer:{
        display:'flex',
        height: '6vh'
    },
    bodyContainer:{
        display:'flex',
        flexDirection: 'row',
        height:'90vh',
        padding: '1vw'
    },
    menuContainer:{
        backgroundColor: 'white',
        marginTop: '6vh'
        
    },
    contextContainer:{
        backgroundColor: '#ddebde',
    
    },
    menuPaper:{
        display: 'flex',
        flexDirection: 'column',
        maxHeight: '70vh',
    },
    contextPaper:{
        display: 'flex',
        flexDirection: 'column',
        maxHeight: '70vh',
        overflow: 'scroll'
}
})

interface IProps{
    
}

const PageLayout: React.FC<IProps> = (props) => {

    const {children} = props

    const useStyle = styles();

    return(
        <Grid
            container
            className={useStyle.root}>
            <Grid
                container
                className={useStyle.headerContainer}>
                    <HeaderBar/>
            </Grid>
            <Grid
                container
                className={useStyle.bodyContainer}>
                <Grid
                    item
                    xs={6}
                    sm = {3}
                    md = {2}
                    xl = {2}
                    className={useStyle.menuContainer}>
                    <Paper className={useStyle.menuPaper}>
                        <SideMenu/>
                    </Paper>
                </Grid>
                <Grid
                    item
                     xs={6}
                     sm = {9}
                     md = {10}
                     xl = {10}
                    className={useStyle.contextContainer}>
                   
                        {children}
                    
                </Grid>
            </Grid>
            
        </Grid>
    )
}

export default PageLayout;
