import React from 'react'
import {IIngredient} from '../../_interfaces/IMenu'
import {Grid, makeStyles, TextField} from '@material-ui/core'
import MenuInputComponent from './MenueInputComponent'

const styles = makeStyles({
    root:{
        display: 'flex',
        flexDirection: 'column'
    }
})

interface IProps{
    ingredient: IIngredient | null
    isReadOnly: boolean
}


const IngrdientListtem: React.FC<IProps> = (props) => {

    const {ingredient, isReadOnly} = props
    const useStyle = styles()

    return(
       <Grid
            className={useStyle.root}
            container>
            <MenuInputComponent
                label='Amount'
                value={ingredient?.amount?? ''}
                disabled= {isReadOnly}/>
             <MenuInputComponent
                label='Name'
                value={ingredient?.name?? ''}
                disabled= {isReadOnly}/>
        </Grid>
    )
}

export default IngrdientListtem
