import { ContactSupportOutlined } from "@material-ui/icons";
import { rejects } from "assert";
import { resolve } from "path/posix";
import React from "react";
import apiConfig from '../_config/apiConfig.json'

export interface IImageUploadResult{
    buffer: string | ArrayBuffer | null
    getBase64: (file: File) => void
}

export const useImageUpload = (src: ArrayBuffer | string): IImageUploadResult =>{

    const [image, setImage] = React.useState<ArrayBuffer|string>(src);    

    const callBack = (buffer: ArrayBuffer) =>{
        setImage(buffer)
    }

    const fetchData = async (form: any) =>{
        await fetch(apiConfig.baseUrl + apiConfig.cookingBook.image, {method: 'POST', mode: 'cors', body: JSON.stringify(form)}) // headers: {"Content-Type":"application/json"}})
    }

    const loadData = async (file: File) => {
        return new Promise((resolve, reject) =>{
            const reader = new FileReader()
            reader.readAsDataURL(file)

            reader.onload = () =>{
                resolve(reader.result)
            }

            reader.onerror = () => {
                reject("Could not load image!")
            }
        })
    }

    const getFormData = (file: File) =>{
        const formData = new FormData();
        formData.append("image", file)
        
        return formData
    }
    const getBase64 = React.useCallback(async (file: File) => {
        console.log(file)
        const data = new FormData()
        data.append("image", file.name) // await loadData(file)
        console.log("Data:" ,data)

        await fetchData(data)
        
    },[])

    return {
        buffer: image,
        getBase64
    }
}
