import React from 'react'
import PageBodyLayout from '../../../_layout/pageBodyLayout'
import MenuInputComponent from '../MenueInputComponent'
import {IMenu} from '../../../_interfaces/IMenu'

interface IProps{

}

const AddMenuContainer: React.FC<IProps> = (props) =>{

    const [menu, setMenu] = React.useState<IMenu>({} as IMenu)

    console.log(menu)
    const onTitleChanged = React.useCallback((name: string) =>{
        const data: IMenu = {...menu, name}
        setMenu(data)
    },[menu])

    const onDescriptionChanged = React.useCallback((description: string) =>{
        const data: IMenu = {...menu, description}
        setMenu(data)
    },[menu])

    const onHowToChanged = React.useCallback((howTo: string) =>{
        const data: IMenu = {...menu, howTo}
        setMenu(data)
    },[menu])

    return(
        <PageBodyLayout
            hasCancelBtn = {true}
            hasSaveBtn = {true}>
               <MenuInputComponent
                    label='Name'
                    value={menu?.name?? ""} 
                    isReadonly={false}
                    onValueChanged={onTitleChanged} />
               <MenuInputComponent
                    label='Description'
                    isMultiRow={true}
                    maxRows={5}
                    fullWidth={true} 
                    value={menu?.description?? ""} 
                    isReadonly={false}
                    onValueChanged={onDescriptionChanged}/>
                <MenuInputComponent
                    label='How To'
                    isMultiRow={true}
                    maxRows={5}
                    fullWidth={true} 
                    value={menu?.howTo?? ""} 
                    isReadonly={false}
                    onValueChanged={onHowToChanged}/>
        </PageBodyLayout>
    )
}

export default AddMenuContainer
