import React from 'react'
import {Grid, makeStyles} from '@material-ui/core'
import HeaderBar from '../_components/_appBars/headerBar'
import SideMenu from '../_components/_appBars/sideMenu'

const styles = makeStyles({
    root:{
        display:'flex'
    },
    headerContainer:{
        display:'flex',
        height: '6vh'
    },
    bodyContainer:{
        display:'flex',
        flexDirection: 'row',
        height:'90vh'
    },
    menuContainer:{
        backgroundColor: 'white',
        marginTop:'5vh',
        maxWidth:'30vw',
        width: '30vw'
    },
    contextContainer:{
        backgroundColor: 'green',
        width:'70vw'
    }
})

interface IProps{
    title: string
}

const PageLayout: React.FC<IProps> = (props) => {

    const {title, children} = props

    const useStyle = styles();
    return(
        <Grid
            container
            className={useStyle.root}>
            <Grid
                container
                className={useStyle.headerContainer}>
                    <HeaderBar/>
            </Grid>
            <Grid
                container
                className={useStyle.bodyContainer}>
                <Grid
                    item
                    //xs={4}
                    className={useStyle.menuContainer}>
                        <SideMenu/>
                </Grid>
                <Grid
                    item
                    // xs={8}
                    className={useStyle.contextContainer}> 
                     {children}
                </Grid>
               
            </Grid>
            
        </Grid>
    )
}

export default PageLayout;
