import React from 'react'
import {AppBar, Grid, IconButton, Toolbar, Typography } from '@material-ui/core'
import { Menu } from '@material-ui/icons'
import { makeStyles } from '@material-ui/styles'

const styles = makeStyles({
    icon:{
        color: 'white'
    }
})
interface IProps{
    title: string
}

const HeaderBar: React.FC<IProps> = (props) =>{

    const {title} = props
    const useStyle = styles()

    const [menuAnchor, setMenuAnchor] = React.useState<HTMLElement|null>(null)

    const handleShowMenu = React.useCallback((e: React.MouseEvent<HTMLElement>) =>{
        setMenuAnchor(e.currentTarget)
    },[])

    const handleHideMenu = React.useCallback(() =>{
        setMenuAnchor(null)
    },[])

    return(
        <AppBar color='primary' position='sticky'>
            <Toolbar>
                <Grid item xs = {4}>
                <IconButton onClick = {handleShowMenu}>
                    <Menu className={useStyle.icon} />
                </IconButton>
                </Grid>
                <Grid item xs = {4}>
                    <Typography variant='h6'>{title}</Typography>
                </Grid>
                <Grid item xs = {4}>
                   
                </Grid>
            </Toolbar>
        </AppBar>
    )
}

export default HeaderBar
