import React from 'react'
import {AppBar, Toolbar, Tooltip, Typography} from '@material-ui/core'
import { useDispatch, useSelector } from 'react-redux'
import { ActionType, AppStateActionTypes, Set_PageTitle } from '../../_redux/_appStateStore/actionTypes';
import { IAppState } from '../../_interfaces/IAppState';
import { Link } from 'react-router-dom';

const HeaderBar: React.FC = () =>{

    const title = useSelector<IAppState, string>((state) => state.pageTitle);

    const dispatch = useDispatch()

    const setPageTitle = (title: string): ActionType =>{
        return{
            type: AppStateActionTypes.SETPAGETITLE,
            payload: title
        }
    }

    return(
        
        <AppBar color='primary' position='sticky'>
            <Toolbar style={{display: 'flex'}}>
                <Tooltip title = 'Home'>
                    <Link style={{textDecoration: 'none', color:'white'}} to = "/" onClick={setPageTitle.bind("Home")}>
                        <Typography variant="h6" component="div" >
                            Family Buttler
                        </Typography>
                    </Link>
                </Tooltip>
                <Typography style = {{marginLeft: '2vw'}} variant="h6" component="div" >
                            {title}
                </Typography>
            </Toolbar>
        </AppBar>

    )
}

export default HeaderBar
