import React from 'react'
import MenuInputComponent from './MenueInputComponent'
import {IMenu} from '../../_interfaces/IMenu'
import { MenuTypeEnum } from '../../_enums/menuEnums'
import { Grid, makeStyles, MenuItem, List, Typography } from '@material-ui/core'
import IngrdientListtem from './IngredientListItem'

interface IProps{
    selectedMenu: IMenu
    isReadOnly: boolean
}

const styles = makeStyles({
    rootContainer:{
        display: 'flex',
        flexDirection: 'column',
        width:'auto',
        height:'auto',
        maxWidth: '82vw',
        overflow:'hidden'
    },
    menuContainer:{
        maxHeight: '60vh',
        width: '60vw',
    },
    ingredientContainer:{
        maxHeight:'40Vh',
        width: '65vw',
        overflow:'hidden'
    },
    ingredientList:{
        width:'82vw',
        overflow:'hidden'
    },
    actionContainer:{

    }
})

const MenuContainer: React.FC<IProps> = (props) =>{

    const {selectedMenu, isReadOnly} = props

    const [menu, setMenu] = React.useState<IMenu>(selectedMenu)
    
    React.useEffect(() =>{
        setMenu(selectedMenu)
    },[setMenu, selectedMenu])

    const original = React.useMemo(() =>{
        return menu
    },[menu])

    const useStyle = styles()

    const onTitleChanged = React.useCallback((name: string) =>{
        const data: IMenu = {...menu, name}
        setMenu(data)
    },[menu])

    const onDescriptionChanged = React.useCallback((description: string) =>{
        const data: IMenu = {...menu, description}
        setMenu(data)
    },[menu])

    const onHowToChanged = React.useCallback((howTo: string) =>{
        const data: IMenu = {...menu, howTo}
        setMenu(data)
    },[menu])

    const onMenuTypeChanged = React.useCallback((value: number) =>{
        const data: IMenu = {...menu, menuType: value}
        setMenu(data)
    },[menu])

    const menuTypes = React.useMemo(() => {
        const elements = [] as JSX.Element[]

        elements.push(<MenuItem key = {-1} id='-1' value={-1} selected={menu.menuType === MenuTypeEnum.None}>Select Menu Type</MenuItem>)
        elements.push(<MenuItem key = {0} id={'0'} value = {0} selected={menu.menuType === MenuTypeEnum.Pasta} >{MenuTypeEnum[MenuTypeEnum.Pasta]}</MenuItem>)
        elements.push(<MenuItem key = {1} id={'4'} value={4} selected={menu.menuType === MenuTypeEnum.Meat} >{MenuTypeEnum[MenuTypeEnum.Meat]}</MenuItem>)
        elements.push(<MenuItem key = {2} id={'3'} value={3} selected={menu.menuType === MenuTypeEnum.Other} >{MenuTypeEnum[MenuTypeEnum.Other]}</MenuItem>)

        return elements
    },[menu.menuType])

    return(
        <Grid
            className={useStyle.rootContainer}
            container>
            <Grid
                className={useStyle.menuContainer}
                container>
                    <MenuInputComponent
                                label='Name'
                                value={menu?.name?? ""} 
                                isReadonly={isReadOnly}
                                onValueChanged={onTitleChanged} />
                    <MenuInputComponent
                                label='Description'
                                isMultiRow={true}
                                maxRows={5}
                                fullWidth={true} 
                                value={menu?.description?? ""} 
                                isReadonly={isReadOnly}
                                onValueChanged={onDescriptionChanged}/>
                    <MenuInputComponent
                                label='How To'
                                isMultiRow={true}
                                maxRows={5}
                                fullWidth={true} 
                                value={menu?.howTo?? ""} 
                                isReadonly={isReadOnly}
                                onValueChanged={onHowToChanged}/>
                    <MenuInputComponent
                                label='Menu Type'
                                isMultiRow={true}
                                maxRows={5}
                                fullWidth={true} 
                                value={menu.menuType ?? -1} 
                                isReadonly={isReadOnly}
                                hasSelect={true}
                                selectElements={menuTypes}
                                onSelectChanged={onMenuTypeChanged}/>
            </Grid>
            {/* ingredients container */}
            <Grid
                className={useStyle.ingredientContainer}
                container>
                <List subheader={<Typography variant='h5'>Zutaten</Typography>} className={useStyle.ingredientList} >
                    {menu?.ingredients?.map((ingredient, index) => {
                        return(
                            <IngrdientListtem key={index} ingredient={ingredient} isReadOnly = {isReadOnly}/>
                        )
                    })}
                </List>
            </Grid>
            <Grid
                className={useStyle.actionContainer}
                container>
                    
            </Grid>
        </Grid>
        //     container>
        //     <Paper
        //         className={useStyle.root}>
        //         <Grid
        //             justifyContent='center'
        //             item
        //             container>
        //             <MenuInputComponent
        //                     label='Name'
        //                     value={menu?.name?? ""} 
        //                     isReadonly={false}
        //                     onValueChanged={onTitleChanged} />
        //                 <MenuInputComponent
        //                     label='Description'
        //                     isMultiRow={true}
        //                     maxRows={5}
        //                     fullWidth={true} 
        //                     value={menu?.description?? ""} 
        //                     isReadonly={false}
        //                     onValueChanged={onDescriptionChanged}/>
        //                 <MenuInputComponent
        //                     label='How To'
        //                     isMultiRow={true}
        //                     maxRows={5}
        //                     fullWidth={true} 
        //                     value={menu?.howTo?? ""} 
        //                     isReadonly={false}
        //                     onValueChanged={onHowToChanged}/>
        //                 <MenuInputComponent
        //                     label='Menu Type'
        //                     isMultiRow={true}
        //                     maxRows={5}
        //                     fullWidth={true} 
        //                     value={menu.menuType ?? -1} 
        //                     isReadonly={false}
        //                     hasSelect={true}
        //                     selectElements={menuTypes}
        //                     onSelectChanged={onMenuTypeChanged}/>
        //         </Grid>
        //                 {menu?.ingredients?.length && (
        //                     <List>
        //                         {menu.ingredients.map((ing, index) =>{
        //                             return(
        //                                 <IngrdientListtem key = {index} ingredient={ing} isReadOnly={false}/>
        //                             )
        //                         })}
        //                     </List>
        //                 )}
        //                 {!menu?.ingredients?.length &&(
        //                     <List subheader={<ListSubheader>Test</ListSubheader>} style={{ maxWidth:'60vw' }}>
        //                         <IngrdientListtem key = {0} ingredient={null} isReadOnly={false}/>
        //                         <IngrdientListtem key = {0} ingredient={null} isReadOnly={false}/>
        //                         <IngrdientListtem key = {0} ingredient={null} isReadOnly={false}/>
        //                         <IngrdientListtem key = {0} ingredient={null} isReadOnly={false}/>
        //                         <IngrdientListtem key = {0} ingredient={null} isReadOnly={false}/>
        //                         <IngrdientListtem key = {0} ingredient={null} isReadOnly={false}/>
        //                         <IngrdientListtem key = {0} ingredient={null} isReadOnly={false}/>
        //                         <IngrdientListtem key = {0} ingredient={null} isReadOnly={false}/>
        //                     </List>
        //                 )}
        //     </Paper>
        // </Grid>
    )
}

export default MenuContainer
