import { Grid, TextField } from '@material-ui/core'
import React from 'react'
import {useImageUpload} from '../../_hooks/useImageUpload'

interface IProps{
    isReadOnly: boolean
    width: number
    height: number
    src: ArrayBuffer | string
    handleImageChanged: (buffer: ArrayBuffer) => void
}

const ImageInput: React.FC<IProps> = (props) =>{

    const {isReadOnly, width, height, src, handleImageChanged} = props

    const imageService = useImageUpload(src)

    const handleChange = React.useCallback(async (e: React.ChangeEvent<HTMLInputElement>) =>{
        e.preventDefault()

        if(e.target.files !== null && e.target.files[0]){

            const file = e.target.files[0]

            imageService.getBase64(file)

            handleImageChanged(imageService.buffer as ArrayBuffer)
        }
    },[imageService, handleImageChanged])

    return(
        <Grid
            style={{display:'flex', flexDirection:'column', justifyContent:'center', alignContent:'center' }}
            container>
            <Grid
                item>
                <img width={width} height={height} src={imageService.buffer?.toString()}/>
            </Grid>
            <Grid
                item>
                {!isReadOnly && (
                    <TextField hidden={true} type='file' onChange={handleChange}/>
                )}
            </Grid>    
        </Grid>
    )
}

export default ImageInput
