import React from 'react'
import PageBodyLayout from '../../../_layout/pageBodyLayout'
import MenuInputComponent from '../MenueTitleComponent'
import {IMenu} from '../../../_interfaces/IMenu'
interface IProps{

}

const AddMenuContainer: React.FC<IProps> = (props) =>{

    const [menu, setMenu] = React.useState<IMenu| null>(null)

    const [menuTitle, setMenuTitle] = React.useState<string>(menu?.name?? "")

    const onTitleChanged = React.useCallback((title: string) =>{
        setMenuTitle(title)
    },[])

    return(
        <PageBodyLayout
            hasCancelBtn = {true}
            hasSaveBtn = {true}>
               <MenuInputComponent title={menuTitle} onTitleChange={onTitleChanged} isReadonly={false}/>
               <MenuInputComponent title={menuTitle} onTitleChange={onTitleChanged} isReadonly={false}/>
        </PageBodyLayout>
    )
}

export default AddMenuContainer
