import React from 'react'
import {Grid, List, ListItem, ListItemText, makeStyles, Typography} from '@material-ui/core'
import { Link } from 'react-router-dom'
import { useSideBarMenuConfig } from '../../_config/sideBarMenuConfig'

const styles = makeStyles({
    root:{
        // marginTop: '.5vh'
    },
    menuItem:{
        width: '100%',
        color: 'black',
        '&:hover':{
            cursor: 'pointer',
        }
    },
    link:{
        textDecoration: 'none',
        paddingLeft:'2vw',
        color: 'black',
        fontSize: '2vh'
    }
})

const SideMenu: React.FC = () =>{

    const useStyle = styles()

    const sideBarConfig = useSideBarMenuConfig()

    const sideBarMenu = React.useMemo(() =>{

        return(
            <List>
                {sideBarConfig.map((config) =>{
                    return(
                        <Grid
                            container>
                            <ListItem key={config.key} className={useStyle.menuItem} selected = {config.expanded} onClick = {config.handleSelected.bind(null, config.key)}>
                                <ListItemText>
                                    <Typography variant='h5'>
                                        {config.title}
                                    </Typography>
                                </ListItemText>
                            </ListItem>
                            {config.expanded && (
                                config.items.map((item) =>{
                                    return(
                                        <ListItem 
                                            key={item.key} 
                                            className={useStyle.menuItem} 
                                            selected = {item.selected}
                                            disabled = {item.disabled} 
                                            onClick = {item.handleSelected}>         
                                            <Link className = {useStyle.link} to = {!item.disabled ? item.path : '#'} >
                                                <ListItemText>
                                                    <Typography variant='h6'>
                                                        {item.value}
                                                    </Typography>
                                                </ListItemText>
                                            </Link>
                                        </ListItem>
                                    )
                                })
                            )}
                        </Grid>
                    )
                })}

            </List>
        )
    },[sideBarConfig]) 

    return(
        <Grid
            className = {useStyle.root}
            container>
                {sideBarMenu}
        </Grid>
    )
}

export default SideMenu;
