import React from 'react'
import {Box, Drawer, Grid, List, ListItem, ListItemIcon, ListItemText, makeStyles} from '@material-ui/core'
import { Link } from 'react-router-dom'
import { useSideBarMenuConfig } from '../../_config/sideBarMenuConfig'
import {Menu} from '@material-ui/icons'

const styles = makeStyles({
    root:{
       display:'flex',
    },
    header:{
        display:'flex',
        justifyContent: 'flex-end',
        justifyItems:'flex-end'
    },
    menuItem:{
        width: '100%',
        color: 'black',
        '&:hover':{
            cursor: 'pointer',
        }
    },
    itemTxt:{
        fontSize: '2em'
    },
    link:{
        textDecoration: 'none',
        paddingLeft:'2vw',
        color: 'black',
        fontSize: '2em'
    }
})

interface IProps{
    open: boolean
    handleClose: () => void
}

const SideMenu: React.FC<IProps> = (props) =>{

    const useStyle = styles()
    const {open, handleClose} = props

    const sideBarConfig = useSideBarMenuConfig()

    const sideBarMenu = React.useMemo(() =>{

        return(
            <List>
                {sideBarConfig.map((config, index) =>{
                    return(
                        <Grid
                            key={index}
                            className={useStyle.root}
                            container>
                            <ListItem key={config.key} className={useStyle.menuItem} selected = {config.expanded} onClick = {config.handleSelected.bind(null, config.key)}>
                                <ListItemText className = {useStyle.itemTxt}>
                                        {config.title}
                                </ListItemText>
                            </ListItem>
                            {config.expanded && (
                                config.items.map((item) =>{
                                    return(
                                        <ListItem 
                                            key={item.key} 
                                            className={useStyle.menuItem} 
                                            selected = {item.selected}
                                            disabled = {item.disabled} 
                                            onClick = {item.handleSelected.bind(null, item.key)}>         
                                            <Link className = {useStyle.link} to = {!item.disabled ? item.path : '#'} >
                                                <ListItemText className = {useStyle.itemTxt}>
                                                        {item.value}
                                                </ListItemText>
                                            </Link>
                                        </ListItem>
                                    )
                                })
                            )}
                        </Grid>
                    )
                })}

            </List>
        )
    },[sideBarConfig, useStyle]) 

    return(
        <Drawer
            className={useStyle.root}
            open={open}
            onClose={handleClose}>
            <Grid
                style={{display: 'flex', padding:'1vw'}}
                item
                container>
                <Grid
                    className={useStyle.header}>
                    <ListItemIcon>
                        <Menu/>
                    </ListItemIcon>
                </Grid>
            </Grid>
            <Grid
                style={{display: 'flex', padding:'1vw'}}
                item
                container>
                {sideBarMenu}
            </Grid>
        </Drawer>
    )
}

export default SideMenu;
