import { Grid, TextField } from '@material-ui/core'
import React from 'react'
import {useImageUpload} from '../../_hooks/useImageUpload'
import noImg from '../../img/noImg.svg'

interface IProps{
    isReadOnly: boolean
    width: number
    height: number
    src?: string
}

const ImageInput: React.FC<IProps> = (props) =>{

    const {isReadOnly, width, height, src} = props

    const [imageSrc, setImageSrc] = React.useState<string | undefined>(src?? noImg)
    
    const imageService = useImageUpload()

    const handleChange = React.useCallback(async (e: React.ChangeEvent<HTMLInputElement>) =>{
        
        if(e.target.files !== null && e.target.files[0]){
           
            const file = e.target.files[0]
            imageService.getBase64(file) 
            
            setImageSrc(imageService.buffer?.toString())
        }

    },[imageService])

    return(
        <Grid
            item>
            <img width={width} height={height} src={imageSrc}/>
            {!isReadOnly && (
                <TextField type='file' onChange={handleChange}/>
            )}
        </Grid>
    )
}

export default ImageInput
