import React from 'react'
import PageBodyLayout from '../../../_layout/pageBodyLayout'
import MenuInputComponent from '../MenueInputComponent'
import {IMenu} from '../../../_interfaces/IMenu'
import { MenuTypeEnum } from '../../../_enums/menuEnums'
import { MenuItem } from '@material-ui/core'

interface IProps{

}

const AddMenuContainer: React.FC<IProps> = (props) =>{

    const [menu, setMenu] = React.useState<IMenu>({} as IMenu)

    console.log(menu)
    const onTitleChanged = React.useCallback((name: string) =>{
        const data: IMenu = {...menu, name}
        setMenu(data)
    },[menu])

    const onDescriptionChanged = React.useCallback((description: string) =>{
        const data: IMenu = {...menu, description}
        setMenu(data)
    },[menu])

    const onHowToChanged = React.useCallback((howTo: string) =>{
        const data: IMenu = {...menu, howTo}
        setMenu(data)
    },[menu])

    const onMenuTypeChanged = React.useCallback((value: number) =>{
        console.log("Change....")
        const type = parseInt(MenuTypeEnum[value]) ?? -1

        const data: IMenu = {...menu, menuType: value}
        setMenu(data)
    },[menu])

    const menuTypes = React.useMemo(() => {
        const elements = [] as JSX.Element[]

        elements.push(<MenuItem key = {-1} id='-1' value='-1' selected={menu.menuType === undefined ? true : false}>Select Menu Type</MenuItem>)
        elements.push(<MenuItem key = {0} id={MenuTypeEnum.Pasta.toString()} value = {MenuTypeEnum.Pasta.toString()}>{MenuTypeEnum[MenuTypeEnum.Pasta]}</MenuItem>)
        elements.push(<MenuItem key = {1} id={MenuTypeEnum.Meat.toString()} value={MenuTypeEnum.Meat.toString()}>{MenuTypeEnum[MenuTypeEnum.Meat]}</MenuItem>)
        elements.push(<MenuItem key = {2} id={MenuTypeEnum.Other.toString()} value={MenuTypeEnum.Other.toString()} >{MenuTypeEnum[MenuTypeEnum.Other]}</MenuItem>)

        return elements
    },[])

    return(
        <PageBodyLayout
            hasCancelBtn = {true}
            hasSaveBtn = {true}>
               <MenuInputComponent
                    label='Name'
                    value={menu?.name?? ""} 
                    isReadonly={false}
                    onValueChanged={onTitleChanged} />
               <MenuInputComponent
                    label='Description'
                    isMultiRow={true}
                    maxRows={5}
                    fullWidth={true} 
                    value={menu?.description?? ""} 
                    isReadonly={false}
                    onValueChanged={onDescriptionChanged}/>
                <MenuInputComponent
                    label='How To'
                    isMultiRow={true}
                    maxRows={5}
                    fullWidth={true} 
                    value={menu?.howTo?? ""} 
                    isReadonly={false}
                    onValueChanged={onHowToChanged}/>
                 <MenuInputComponent
                    label='MenuType'
                    isMultiRow={true}
                    maxRows={5}
                    fullWidth={true} 
                    value={MenuTypeEnum[menu?.menuType]?? -1} 
                    isReadonly={false}
                    hasSelect={true}
                    selectElements={menuTypes}
                    onSelectChanged={onMenuTypeChanged}/>
        </PageBodyLayout>
    )
}

export default AddMenuContainer
