import React from "react";

export interface IImageUploadResult{
    buffer: string | ArrayBuffer | null
    getBase64: (file: File) => void
}

export const useImageUpload = (src: string): IImageUploadResult =>{

    const [image, setImage] = React.useState<string | ArrayBuffer | null>(src);    

    const callBack = (buffer: string | ArrayBuffer | null) =>{
        setImage(buffer)
    }

    const getBase64 = React.useCallback((file: File) => {
        let reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = async  () => {
            callBack(reader.result)
        };
        reader.onerror = function (error) {
            console.log('Error: ', error);
        };
    },[])

    return {
        buffer: image,
        getBase64
    }
}
