import React from 'react'
import useApi from '../../_hooks/useApi'
import { IMenuResponse } from '../../_interfaces/IMenu'
import apiConfig from '../../_config/apiConfig.json'
import { SetMenuCollection } from '../../_redux/_appStateStore/appStoreAccessor'
import { useDispatch } from 'react-redux'
import AddMenuContainer from './_add/AddMenuContainer'
import EditMenuContainer from './_edit/EditMenuContainer'


interface IProps{
    componentKey: 'view' | 'add' | 'edit'
}

const CookingBookDataService: React.FC<IProps> = (props) =>{

    const {componentKey} = props

    const dispatch = useDispatch();
    
    const menuDataService = useApi<IMenuResponse>({serviceUri: apiConfig.baseUrl + apiConfig.cookingBook.getMenuCollection, params: null})
    
    const component = React.useMemo(() =>{

        switch(componentKey){
            case 'view':
                return <AddMenuContainer/>
            case 'add':
                return <AddMenuContainer/>
            case 'edit':
                return <EditMenuContainer/>
        }
    },[componentKey])

    if(menuDataService.items[0] === null){

        return null
    }

    dispatch(SetMenuCollection(menuDataService.items[0]?.menuCollection))
    
    


    return component
}

export default CookingBookDataService
