import { Grid, TextField, CircularProgress } from '@material-ui/core'
import React from 'react'
import { IImageUploadResult, useImageConverter } from '../../_hooks/useImageConverter'

interface IProps{
    isReadOnly: boolean
    width: number
    height: number
    isLoading: boolean
    noImageSrc: string
    imageSrc: string
    onImageChanged: (image: string) => void
}

const ImageInput: React.FC<IProps> = (props) =>{

    const {isReadOnly, width, height, isLoading, imageSrc, noImageSrc, onImageChanged} = props
    const imageService = useImageConverter("")
    
    const src = React.useMemo(() => {
        if(imageSrc === undefined || imageSrc === ""){
            return noImageSrc
        }

        return imageSrc
    },[imageSrc, noImageSrc])

    const handleChange = React.useCallback(async (e: React.ChangeEvent<HTMLInputElement>) => {
    
        await imageService.handleChange(e)
        .then(() =>{
            if(imageService.image !== undefined)
            onImageChanged("data:image/png;base64," + imageService.image)
        })
    },[imageService, onImageChanged])

    if(imageSrc === undefined || imageSrc === ""){
        return null
    }
    
    return(
        <Grid
            style={{display:'flex', flexDirection:'column', justifyItems:'center', alignItems:'center' }}
            item>
            <Grid
                 xs={12}
                 sm={4}
                item>
                <img width={width} height={height} src={src} alt=''/>
            </Grid>
            <Grid
                xs={12}
                sm={4}
                item>
                {!isReadOnly && (
                    <TextField hidden={true} type='file' onChange={handleChange}/>
                )}
            </Grid>
            {isLoading && (
                <CircularProgress/>
            )}    
        </Grid>
    )
}

export default ImageInput
