import { ListItem, ListItemText } from '@material-ui/core'
import { makeStyles } from '@material-ui/styles'
import React, { CSSProperties } from 'react'
import {List} from 'react-virtualized'
import { IMenu } from '../../_interfaces/IMenu'

interface IProps{
    menuCollection: IMenu[]
    selectedMenuId: number
    handleSelectMenu: (id: number) => void
}

const styles = makeStyles({
    item: {
        '&:hover':{
            cursor: 'pointer'
        }
    }
})

const MenuList: React.FC<IProps> = (props) =>{

    const {menuCollection, selectedMenuId, handleSelectMenu} = props

    const listHeight = window.screen.availHeight/100 * 80
    const listWidth = window.screen.availWidth/100 * 20
    const useStyle = styles()

    const renderRow = React.useCallback((key: string, index: number, style: CSSProperties) =>{
        return(
            <ListItem 
                key={key} 
                id={menuCollection[index].id.toString()}
                className={useStyle.item}
                style={style}
                selected ={selectedMenuId === menuCollection[index].id}
                onClick = {handleSelectMenu.bind(null, menuCollection[index].id)}>
                <ListItemText>
                    {menuCollection[index].name}
                </ListItemText>
            </ListItem>
        )
    },[handleSelectMenu, menuCollection, selectedMenuId, useStyle])

    return(
        <List
            height={listHeight}
            width={listWidth}
            rowHeight={20}
            rowCount={menuCollection?.length?? 20}
            rowRenderer={(r) => renderRow(r.key, r.index, r.style)}/>
    )
}

export default MenuList
