import React from "react";
import apiConfig from '../_config/apiConfig.json'

export interface IImageUploadResult{
    buffer: string | ArrayBuffer | null
    file: FormData|null
    getBase64: (file: File) => void
}

export const useImageUpload = (src: ArrayBuffer | string): IImageUploadResult =>{

    const [image, setImage] = React.useState<ArrayBuffer|string>(src);    
    const [file, setFile] = React.useState<FormData|null>(null)

    const callBack = (buffer: ArrayBuffer) =>{
        setImage(buffer)
    }

    const fetchData = async (form: FormData) =>{
        await fetch(apiConfig.baseUrl + apiConfig.cookingBook.image, {method: 'POST', mode: 'cors', body: JSON.stringify(form), headers: {"Content-Type":"application/json"}})
    }

    const getBase64 = React.useCallback((file: File) => {
        let reader = new FileReader();
        
        reader.readAsDataURL(file);
        reader.onload = async  () => {

            const result = reader.result as ArrayBuffer
            
            const array = new Uint32Array(result)
            // const s = reader.readAsBinaryString(file)

            console.log(array)
            // callBack(reader.result as ArrayBuffer)
        };
        reader.onerror = function (error) {
            console.log('Error: ', error);
        };
    },[])

    return {
        buffer: image,
        file: file,
        getBase64
    }
}
