import { Grid, TextField } from '@material-ui/core'
import React from 'react'
import {useImageUpload} from '../../_hooks/useImageUpload'

interface IProps{
    isReadOnly: boolean
    width: number
    height: number
    src: string
    handleImageChanged: (image: string) => void
}

const ImageInput: React.FC<IProps> = (props) =>{

    const {isReadOnly, width, height, src, handleImageChanged} = props

    const imageService = useImageUpload("")
  
    const imageSrc = React.useMemo(() =>{
        
        let imageSrc = src.length > 0 ? src : ""
        
        if(imageService.image === undefined){
            return imageSrc 
        }

        imageSrc = "data:image/png;base64," + imageService.image

        handleImageChanged(imageSrc)

        return imageSrc
    },[imageService.image, src])

    return(
        <Grid
            style={{display:'flex', flexDirection:'column', justifyContent:'center', alignContent:'center' }}
            container>
            <Grid
                item>
                <img width={width} height={height} src={imageSrc}/>
            </Grid>
            <Grid
                item>
                {!isReadOnly && (
                    <TextField hidden={true} type='file' onChange={imageService.handleChange}/>
                )}
            </Grid>    
        </Grid>
    )
}

export default ImageInput
