import { Grid, TextField, CircularProgress } from '@material-ui/core'
import React from 'react'
import {IImageUploadResult} from '../../_hooks/useImageConverter'

interface IProps{
    isReadOnly: boolean
    width: number
    height: number
    src: string
    imageService: IImageUploadResult
    handleImageChanged: (image: string) => void
}

const ImageInput: React.FC<IProps> = (props) =>{

    const {isReadOnly, width, height, src, imageService, handleImageChanged} = props
  
    const imageSrc = React.useMemo(() =>{
        
        let imageSrc = src.length > 0 ? src : ""
        
        if(imageService.image === undefined){
            imageService.setLoadingState(false)
            return imageSrc 
        }

        imageSrc = "data:image/png;base64," + imageService.image

        handleImageChanged(imageSrc)
        imageService.setLoadingState(false)
        return imageSrc
    },[imageService.image, src])

    return(
        <Grid
            style={{display:'flex', flexDirection:'column', justifyItems:'center', alignItems:'center' }}
            item>
            <Grid
                 xs={12}
                 sm={4}
                item>
                <img width={width} height={height} src={imageSrc}/>
            </Grid>
            <Grid
                xs={12}
                sm={4}
                item>
                {!isReadOnly && (
                    <TextField hidden={true} type='file' onChange={imageService.handleChange}/>
                )}
            </Grid>
            {imageService.isLoading && (
                <CircularProgress/>
            )}    
        </Grid>
    )
}

export default ImageInput
