import React from 'react'
import {makeStyles, TextField} from '@material-ui/core'

interface IProps{
    value: string
    type?: string
    label: string
    maxRows?: number
    multiLine?: boolean
    fullWidth?: boolean
    onValueChange: (value: string) => void
}

const InputTextField: React.FC<IProps> = (props) =>{

    const {value, type, label, maxRows, multiLine, fullWidth, onValueChange} = props

    const handleValueChanged = React.useCallback((e: React.ChangeEvent<HTMLInputElement>) =>{
        onValueChange(e.currentTarget.value as string)
    },[])

    return(
        <TextField
            fullWidth = {fullWidth}
            multiline={multiLine}
            maxRows={maxRows}
            label = {label}
            type={type?? 'text'}
            value={value} 
            onChange={handleValueChanged}/>
    )
}

export default InputTextField
