import React from 'react'
import useApi from '../../_hooks/useApi'
import { IMenuResponse } from '../../_interfaces/IMenu'
import apiConfig from '../../_config/apiConfig.json'
import { SetMenuCollection } from '../../_redux/_appStateStore/appStoreAccessor'
import { useDispatch } from 'react-redux'
import MenuLayout from './menuLayout'
import {IMenuLayoutConfig} from './interfaces/IMenueLayoutConfig'

interface IProps{
    componentKey: 'view' | 'add' | 'edit'
}

const CookingBookDataService: React.FC<IProps> = (props) =>{

    const {componentKey} = props

    const dispatch = useDispatch();
    
    const menuDataService = useApi<IMenuResponse>({serviceUri: apiConfig.baseUrl + apiConfig.cookingBook.getMenuCollection, params: null})
    
    const component = React.useMemo(() =>{
        var config: IMenuLayoutConfig = {isReadOnly: true, hasFilter: true, hasMenuList: true}
        switch(componentKey){
            case 'view':
                config = {
                    isReadOnly: true, 
                    hasFilter: true, 
                    hasMenuList: false
                }
                return <MenuLayout layoutConfig={config} />
            case 'add':
                config = {
                    isReadOnly: false, 
                    hasFilter: false, 
                    hasMenuList: true
                }
                return <MenuLayout layoutConfig={config}/>
            case 'edit':
                config = {
                    isReadOnly: false, 
                    hasFilter: true, 
                    hasMenuList: true
                }
                return <MenuLayout layoutConfig={{isReadOnly: false, hasFilter: true, hasMenuList: true}}/>
        }
    },[componentKey])

    if(menuDataService.items[0] === null){

        return null
    }

    dispatch(SetMenuCollection(menuDataService.items[0]?.menuCollection))
    
    return component
}

export default CookingBookDataService
