import React from 'react'
import {Divider, Drawer, Grid, ListItem, ListItemIcon, ListItemText, makeStyles} from '@material-ui/core'
import { Link } from 'react-router-dom'
import { Close } from '@material-ui/icons'
import { ISidebarMenuConfig } from '../../_interfaces/ISidebarMenuConfig'
import useApi from '../../_hooks/useApi'

const styles = makeStyles({
    root:{
        marginTop: '3vh'
        // display:'flex',
        // flexDirection:'column',
        // padding: '1vw'
    }
})

interface IProps{
    open: boolean
    handleClose: () => void
}

const SideMenu: React.FC<IProps> = (props) =>{

    const {open, handleClose} = props
    const useStyle = styles()

    const dataService = useApi({serviceUri:"http://familybuttler:55321/api/I18n/GetValues/app_appMenuEn", params: null});
    const [selectedMenu, setSelectedMenu] = React.useState<string>("");
    const [selectedSubMenu, setSelectedSubMenu] = React.useState<string>("");

    const handleSelectMenu = React.useCallback((key: string) =>{
        setSelectedMenu(key)
    },[])

    const handleSelectSubMenu = React.useCallback((key: string) =>{
        setSelectedSubMenu(key)
    },[])

    const menuConfig = React.useMemo(() : ISidebarMenuConfig[] => {
        const menuValues = JSON.parse(JSON.stringify(dataService.items))[0]

        const config = [] as ISidebarMenuConfig[]

        const cookingBookConfig: ISidebarMenuConfig = {
            title: menuValues?.menuItemCookingBook,
            key: menuValues?.menuItemCookingBook,
            expanded: selectedMenu === menuValues?.menuItemCookingBook,
            handleSelected: handleSelectMenu.bind(menuValues?.menuItemCookingBook),
            items: [{
                key: menuValues?.menuSubItemAdd,
                value: menuValues?.menuSubItemAdd,
                selected: selectedSubMenu === menuValues?.menuSubItemAdd,
                path: '/cookingbook/add',
                disabled: false,
                pageTitle: "",
                sortOrder: 1,
                handleSelected: handleSelectSubMenu.bind(menuValues?.menuSubItemAdd)

            },
            {
                key: menuValues?.menuSubItemEdit,
                value: menuValues?.menuSubItemEdit,
                selected: selectedSubMenu === menuValues?.menuSubItemEdit,
                path: '/cookingbook/edit',
                disabled: false,
                pageTitle: "",
                sortOrder: 2,
                handleSelected: handleSelectSubMenu.bind(menuValues?.menuSubItemEdit)

            },
            {
                key: menuValues?.menuSubItemView,
                value: menuValues?.menuSubItemView,
                selected: selectedSubMenu === menuValues?.menuSubItemView,
                path: '/cookingbook/add',
                disabled: false,
                pageTitle: "",
                sortOrder: 0,
                handleSelected: handleSelectSubMenu.bind(menuValues?.menuSubItemView)

            }]
        }

        config.push(cookingBookConfig)

        return config

    },[dataService.items])

    if(!dataService.dataBound){
        return null
    }
    
    console.log(dataService.items)
    
   
   

    console.log(menuConfig)

    return(
        <Drawer
            anchor='left'
            open = {open}
            onClose={handleClose}>
            <Grid
                className={useStyle.root}
                container>
                <ListItem onClick={handleClose}>
                    <ListItemIcon>
                        <Close/>
                    </ListItemIcon>
                    {open &&(
                    <ListItemText>
                        <Link to='/'>Back</Link>
                    </ListItemText>
                    )}
                </ListItem>
                <ListItem onClick={handleClose}>
                    <ListItemIcon>
                        <Close/>
                    </ListItemIcon>
                    {open && (
                    <ListItemText>
                        <Link to='/1'>To</Link>
                    </ListItemText>
                    )}
                </ListItem>
            </Grid>
        </Drawer>
    )
}

export default SideMenu;
