import React from "react";
import apiConfig from '../_config/apiConfig.json'

export interface IImageUploadResult{
    image: Blob | undefined
    handleChange: (e: React.ChangeEvent<HTMLInputElement>) => Promise<void>
}

export const useImageConverter = (src: ArrayBuffer | string): IImageUploadResult =>{

    const [image, setImage] = React.useState<Blob>();    
    const [resolved, setResolved] = React.useState<boolean>(false)
    
    const resolveResponse =  React.useCallback(async (res: Response) =>{

        await Promise.resolve(res.json())
        .then((data) =>{

            var img: Blob = JSON.parse(JSON.stringify(data))
            setImage(img)
           
        })

    },[])

    const fetchData = React.useCallback(async (form: any) =>{
        await fetch(apiConfig.baseUrl + apiConfig.cookingBook.imageConverter, {method: 'POST', mode: 'cors', body: form})
        .then(async (res) => {
            if(res.status === 200){
                resolveResponse(res)
            }
        })
    },[resolveResponse])

    const getFormData = (file: File) =>{
        const formData = new FormData();
        formData.append("image", file)
        
        return formData
    }

    const getBase64Image = React.useCallback(async (file: File) => {
        
        const formData = getFormData(file)
        
        await fetchData(formData)
       
    },[fetchData])

    const handleChange = React.useCallback(async (e: React.ChangeEvent<HTMLInputElement>) =>{
        e.preventDefault()
        if(e.target.files !== null && e.target.files[0]){

            const file = e.target.files[0]

            await getBase64Image(file)  
        }
    },[getBase64Image])

    return {
        image,    
        handleChange
    }
}
