import { ContactSupportOutlined } from "@material-ui/icons";
import { rejects } from "assert";
import { resolve } from "path/posix";
import React from "react";
import apiConfig from '../_config/apiConfig.json'

export interface IImageUploadResult{
    image: string
    getBase64Image: (file: File) => void
}

export const useImageUpload = (src: ArrayBuffer | string): IImageUploadResult =>{

    const [image, setImage] = React.useState<ArrayBuffer|string>(src);    

    const callBack = (buffer: ArrayBuffer) =>{
        setImage(buffer)
    }

    const fetchData = async (form: any) =>{
        await fetch(apiConfig.baseUrl + apiConfig.cookingBook.imageConverter, {method: 'POST', mode: 'cors', body: form})
    }

    const getFormData = (file: File) =>{
        const formData = new FormData();
        formData.append("image", file)
        
        return formData
    }

    const getBase64Image = React.useCallback(async (file: File) => {
    
        const formData = getFormData(file)
        // await loadData(file)
        console.log("Data:" , formData)

        await fetchData(formData)
        
    },[])

    return {
        buffer: image,
        getBase64Image
    }
}
