import { Grid, TextField } from '@material-ui/core'
import React, { MouseEvent } from 'react'

const LandingPage: React.FC = () => {
    
    const [selectedFile, setFile] = React.useState<string | ArrayBuffer | null>('');

    const callBack = (buffer: string | ArrayBuffer | null) =>{
        console.log(buffer)
        setFile(buffer)
    }

    const getBase64 = (file: File) => {
        let reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = function () {
            callBack(reader.result)
        };
        reader.onerror = function (error) {
            console.log('Error: ', error);
        };
    }

    const handleChange = React.useCallback(async (e: React.ChangeEvent<HTMLInputElement>) =>{
        
        if(e.target.files !== null && e.target.files[0]){
           
            const file = e.target.files[0]
            getBase64(file)
        }

    },[])

   
    return(
        <Grid container>
            <TextField type='file' onChange={handleChange}/>
            <Grid container>
                {selectedFile !== null && (
                    <img src={selectedFile.toString()} width={400} height={400}>

                    </img>
                )}
            </Grid>
        </Grid>
    )
}

export default LandingPage
